st == false then\n            allstates[sourceGUID].stacks = allstates[sourceGUID].stacks - 1\n            if allstates[sourceGUID].stacks == 0 then\n                allstates[sourceGUID].show = false\n                --allstates[sourceGUID] = nil\n            end\n            allstates[sourceGUID].changed = true\n        end\n    elseif subevent == \"SPELL_CAST_SUCCESS\" and sourceName == UnitName(\"player\") and spellName == \"召唤恶魔暴君\" then\n        aura_env.tyrantExist = true\n        aura_env.tyrantStartTime = GetTime()\n        if not select(4, GetTalentInfo(7,2, GetActiveSpecGroup())) then\n            for k,v in pairs(allstates) do\n                allstates[k].duration = allstates[k].duration + aura_env.tyrantDuration\n                allstates[k].expirationTime = allstates[k].expirationTime + aura_env.tyrantDuration\n                allstates[k].index = -allstates[k].expirationTime\n                allstates[k].changed = true\n            end\n        else \n            aura_env.impNumber = 0\n            for k,v in pairs(allstates) do\n                allstates[k].show = false\n                allstates[k].changed = true\n                --allstates[k] = nil\n            end\n        end\n    elseif  subevent == \"SPELL_CAST_SUCCESS\" and sourceName == UnitName(\"player\") and spellName == \"能量虹吸\" then\n        if select(4, GetTalentInfo(2,2, GetActiveSpecGroup())) then\n            local lowest = -1\n            local second_lowest = -1\n            local time = -1\n            -- Remove 2 Imps with lowest duration\n            for k,v in pairs(allstates) do \n                if second_lowest == -1 or allstates[k].expirationTime < time then\n                    second_lowest = k\n                    time = allstates[k].expirationTime\n                    if lowest == -1 or allstates[second_lowest].expirationTime < allstates[lowest].expirationTime then\n                        local temp = lowest\n                        lowest = second_lowest\n          