				["init"] = {
					["custom"] = "--------------------------------------------\n--            SOUL SHARDS BAR             --\n--      Made by Kirihn-Area52 & Ipse      --\n-- \"The headless pigmy has terrible aim.\" --\n--              :PeepoHappy:              --\n--------------------------------------------\n--       For FREE distribution ONLY       --\n--------------------------------------------\n\nlocal e = aura_env\n\n--USER  SETTINGS--\n\n------------------\n--SHARDS DISPLAY--\n------------------\n\ne.DisplayEmptyShards = true\ne.TrackDoom = true --Read bellow\ne.Spacing = 3 \n\ne.PositionShardsManually = false\ne.ShardPositions = {\n    Shard1 = {x = -72, y = 0},\n    Shard2 = {x = -36, y = 0},\n    Shard3 = {x =   0, y = 0},\n    Shard4 = {x =  36, y = 0},\n    Shard5 = {x =  72, y = 0}\n}\n\n--[[\nIf TrackDoom is enabled, Doom timers will show up on the bars,\n    and the shard count display for Demonology will be hidden.\n    If you have suggestions for better managing the display of\n    both numbers, please let me know.\n\nYou can change the way Doom timers look by changing how Right\n    Text looks like.\n]]--\n\n-----------------\n--SHARDS COLORS--\n-----------------\n\n--Provide either ARGB or RGB hex codes\nlocal Affliction = {\n    Foreground = \"#9804F3\",\n    Background = \"000000\",\n    Full  = \"ff3c00\",\n    Spend = \"f0beff\"\n}\nlocal Demonology = {\n    Foreground = \"#9804F3\",\n    Background = \"000000\",\n    Full  = \"ff3c00\",\n    Spend = \"f0beff\",\n    Doom  = \"600070\"\n}\nlocal Destruction = {\n    Foreground = \"FFA208\",\n    Background = \"000000\",\n    Full  = \"ff3c00\",\n    Spend = \"FFFE65\"\n}\nlocal Xoroth = {--Only works when in Destruction spec\n    Foreground = \"FFA208\",\n    Background = \"000000\",\n    Full  = \"ff3c00\",\n    Spend = \"FFFE65\"\n}\n\ne.ColorShardsSeparately = false\n\nlocal SeparateShardsColors = {\n    --[[\n    Colors only the Foreground, other areas are\n               inherited from the tables above.\n    Both ARGB and RGB hex codes are acceptable.\n    ]]--\n    \n    Shard1 = \"e600ff\", \n    Shard2 = \"e600ff\", \n    Shard3 = \"e600ff\", \n    Shard4 = \"9310ff\", \n    Shard5 = \"6432ff\"\n}\n\n---------------\n--SHARDS TEXT--\n---------------\n\nlocal ShardCountOnMiddle = true --Read bellow.\n\nlocal CountDisplayModes = { --Read bellow.\n    Affliction = \"simple\",\n    Demonology = \"simple\",\n    Destruction = \"simple\"\n}\nlocal ShardConsumptionPrediction = {\n    Enabled = true, --If false, everything else is ignored.\n    Text = \"*\", --Read bellow.\n    Color = \"ff3200\" --Use an RGB hex code.\n    --This will also color partial Doom ticks.\n}\n\ne.TextPosition = {\n    AnchorPoint = \"BOTTOM\",\n    --\"CENTER\", \"TOP\", \"BOTTOM\", \"LEFT\", \"RIGHT\",\n    --\"TOPLEFT\", \"TOPRIGHT\", \"BOTTOMLEFT\", or \"BOTTOMRIGHT\"\n    \n    xOffSet = 0, yOffSet = 0,\n    \n    Justification = {\n        Horizontal = \"CENTER\",\n        -- \"CENTER\", \"LEFT\", or \"RIGHT\"\n        Vertical = \"MIDDLE\",\n        -- \"MIDDLE\", \"TOP\", or \"BOTTOM\"\n    },\n}\n\n--[[\nShardCountOnMiddle -> If false, will make the count \n                         appear on the latest shard. \n                      If true, empty shards are hidden,\n                         and you have fewer than 3 shards;\n                         the count will be displayed on \n                         the latest shard.\n\nDisplayModes -> If \"simple\", displays the count as '3' and '3.5'.\n                If \"full\", displays the count as '3.0' and '3.5'.\n                If \"tens\", displays the count as '30' and '35'.\n                If false, will hide the display.\n\nPrediction.Text -> Brackets - such as {}, [], (), <>, and <<>> - \n                      will be correctly mirrored.\n                   For \\, use \"\\\\\". For \", use '\"' \n                      (single - double - single), and vice-versa.\n                   An empty string - i.e., inputing \"\" - \n                      is acceptable.\n                   Other unlisted characters will be used as is.\n]]--\n\n----------------------------\n--No touchrino hereinafter--\n----------------------------\n\ne.cost = 0\ne.consuming = 0\n\nlocal display = false\nlocal brackets = {\n    [\"{\"] = \"}\",\n    [\"}\"] = \"{\",\n    [\"[\"] = \"]\",\n    [\"]\"] = \"[\",\n    [\"(\"] = \")\",\n    [\")\"] = \"(\",\n    [\"<\"] = \">\",\n    [\">\"] = \"<\",\n    [\"<<\"] = \">>\",\n    [\">>\"] = \"<<\",\n}\nlocal spec = {\n    [1] = {\"Affliction\", Affliction},\n    [2] = {\"Demonology\", Demonology},\n    [3] = {\"Destruction\", Destruction},\n    [4] = {\"Destruction\", Xoroth}\n}\n\nlocal Pallete = {}\n\nfunction e.AdjustToSpec(specialization)\n    local function HexToRGBPerc(hex)\n        local string, tonumber = string, tonumber\n        \n        hex = hex:gsub(\"%W\", \"\")\n        \n        if string.len(hex) == 6 then\n            hex = \"ff\"..hex\n        end\n        \n        local ahex, rhex, ghex, bhex = string.sub(hex, 1, 2), string.sub(hex, 3, 4), string.sub(hex, 5, 6), string.sub(hex, 7, 8)\n        \n        return {a = tonumber(ahex, 16)/255, r = tonumber(rhex, 16)/255, g = tonumber(ghex, 16)/255, b = tonumber(bhex, 16)/255}\n    end\n    \n    local s\n    \n    if specialization == 3 and IsPlayerSpell(101508) then\n        s = spec[4]\n    else\n        s = spec[specialization]\n    end\n    \n    display = not (e.TrackDoom and IsPlayerSpell(265412)) and CountDisplayModes[s[1]]\n    table.wipe(Pallete)\n    \n    if e.ColorShardsSeparately then\n        for Shard, Color in pairs(SeparateShardsColors) do\n            Pallete[Shard] = HexToRGBPerc(Color)\n        end\n    end\n    \n    for Area, Color in pairs(s[2]) do\n        if not e.ColorShardsSeparately or Area ~= \"Foreground\" then\n            Pallete[Area] = HexToRGBPerc(Color)\n        end\n    end\n    \n    if s[1] == \"Demonology\" then\n        Pallete.Partial = HexToRGBPerc(ShardConsumptionPrediction.Color)\n    end\n    \n    e.Pallete = Pallete\nend\n\ne.AdjustToSpec(GetSpecialization())\n\nfunction e.FormatCount(pos, shards, consumed)\n    if display then\n        local match = ShardCountOnMiddle and 3 or math.floor(shards - 0.1) + 1\n        local pred = ShardConsumptionPrediction\n        \n        if not e.DisplayEmptyShards and ShardCountOnMiddle and shards <= 2 then\n            match = shards\n        end\n        \n        if pos == (match or math.floor(match - 0.1) + 1) then\n            consumed = pred.Enabled and consumed or 0\n            consumed = shards > consumed and consumed or shards\n            \n            if display == \"simple\" then\n                shards = shards - consumed\n            elseif display == \"full\" then\n                shards = string.format(\"%.1f\", shards - consumed)\n                if shards == \"0.0\" then shards = 0 end\n            elseif display == \"tens\" then\n                shards = (shards - consumed)*10\n            else\n                return \"\"\n            end\n            \n            if pred.Enabled and consumed > 0 then\n                local open, close = pred.Text, brackets[pred.Text] or pred.Text\n                local color = pred.Color\n                \n                color = color:gsub(\"%W\", \"\")\n                \n                local len = string.len(color)\n                \n                if len == 8 then\n                    color = color:gsub(\"^%w%w\", \"ff\")\n                elseif len == 6 then\n                    color = \"ff\"..color\n                else\n                    color = \"ffff3200\"\n                end\n                \n                shards = WrapTextInColorCode(open..shards..close, color)\n            end\n            \n            return shards\n        else\n            return \"\"\n        end\n    else\n        return \"\"\n    end\nend\n\ne.Doom = e.Doom or {}\ne.format = string.format\n\nfunction e.nextDoom(dest1, dest2)\n    return e.Doom[dest1].exp < e.Doom[dest2].exp\nend\n\n--I do not understand this\nlocal function orderednext(t, n)\n    local key = t[t.__next]\n    \n    if not key then return end\n    \n    t.__next = t.__next + 1\n    \n    return key, t.__source[key]\nend\n\nfunction e.orderedDoom(t, f)\n    local keys, kn = {__source = t, __next = 1}, 1\n    \n    for k in pairs(t) do\n        keys[kn], kn = k, kn + 1\n    end\n    \n    table.sort(keys, f)\n    \n    return orderednext, keys\nend\n\n--Forgive our sins\nPigmyShards_DoomTracking = PigmyShards_DoomTracking or {}\n\nif not PigmyShards_DoomTracking.Frame then\n    local frame = CreateFrame(\"Frame\", nil, UIParent)\n    local doom = PigmyShards_DoomTracking\n    \n    doom.Frame = frame\n    \n    doom.TrackDoom = e.TrackDoom\n    doom.DoomInfo = {}\n    doom.UnitDebuff = WA_GetUnitDebuff\n    doom.UnitGUID = UnitGUID\n    \n    doom.Frame:SetSize(1, 1)\n    doom.Frame:SetFrameStrata(\"BACKGROUND\")\n    doom.Frame:RegisterEvent(\"COMBAT_LOG_EVENT_UNFILTERED\")\n    doom.Frame:SetScript(\"OnEvent\", function(self)\n            local _, event, _, source, _, _, _, dest, _, _, _, spellID = CombatLogGetCurrentEventInfo()\n            \n            if source == WeakAuras.myGUID and spellID == 265412 and doom.TrackDoom then\n                doom.DoomInfo[dest] = doom.DoomInfo[dest] or {\n                    exp = 0, dur = 0,\n                    expDot = 0, durDot = 0,\n                    lastTick = 0, nextTick = 0 \n                }\n                \n                local d = doom.DoomInfo[dest]\n                local nameplateCheck = false\n                local now = GetTime()\n                \n                if event == \"SPELL_AURA_APPLIED\" then\n                    d.exp, d.dur = now + 30, 30 \n                    d.expDot, d.durDot = now + 30, 30\n                    d.lastTick, d.nextTick = 0, 0\n                    \n                    for i = 1, 40 do \n                        if doom.UnitGUID(\"nameplate\"..i) == dest then\n                            local npd, npe = select(5, doom.UnitDebuff(\"nameplate\"..i, 265412, \"PLAYER\"))\n                            \n                            nameplateCheck = true\n                            d.exp, d.dur = npe, npd\n                            d.expDot, d.durDot = npe, npd\n                            d.lastTick, d.nextTick = 0, npe\n                            \n                            break\n                        end\n                    end\n                    \n                    if not nameplateCheck then\n                        d.exp, d.dur = now + 30, 30\n                        d.expDot, d.durDot = d.exp, 30\n                        d.lastTick = 0\n                    end\n                end\n                \n                if event == \"SPELL_AURA_REFRESH\" then\n                    nameplateCheck = false\n                    \n                    for i = 1, 40 do \n                        if doom.UnitGUID(\"nameplate\"..i) == dest then\n                            local npd, npe = select(5, doom.UnitDebuff(\"nameplate\"..i, 265412, \"PLAYER\"))\n                            \n                            if d.lastTick > 0 then\n                                d.nextTick = d.lastTick + 30\n                            else\n                                d.lastTick, d.nextTick = d.exp, npe\n                            end\n                            \n                            if d.dur ~= 30 then\n                                d.exp, d.dur = d.lastTick + 30, npd\n                            end\n                            \n                            break\n                        end\n                    end\n                    \n                    if not nameplateCheck then\n                        if d.lastTick > 0 then\n                            d.nextTick = d.lastTick + 30\n                        else\n                            d.lastTick, d.nextTick = d.exp, d.exp + 30\n                        end\n                        \n                        if d.dur ~= 30 then\n                            d.exp, d.dur = d.lastTick + 30, 30\n                        end\n                        \n                        local remains = d.expDot - now\n                        \n                        d.durDot = remains > 9 and 39 or 30 + remains\n                        d.expDot = now + d.durDot\n                    end\n                end\n                \n                if event == \"SPELL_PERIODIC_DAMAGE\" or event == \"SPELL_PERIODIC_MISSED\" then\n                    for i = 1, 40 do \n                        if doom.UnitGUID(\"nameplate\"..i) == dest then\n                            local npe = select(6, doom.UnitDebuff(\"nameplate\"..i, 265412, \"PLAYER\"))\n                            \n                            nameplateCheck = true\n                            \n                            d.exp, d.dur = npe, npe - now\n                            d.lastTick = now\n                            d.nextTick = now + 30\n                            \n                            break\n                        end\n                    end\n                    \n                    if not nameplateCheck then\n                        if now + 30 <= d.expDot then\n                            d.exp = now + 30\n                            d.dur = d.exp - now\n                        else\n                            d.exp, d.dur = d.expDot, d.expDot - now\n                        end\n                        \n                        d.lastTick, d.nextTick = now, now + 30\n                    end\n                end\n                \n                if event == \"SPELL_AURA_REMOVED\" then\n                    d.exp, d.dur = 0, 0\n                    d.expDot, d.durDot = 0, 0\n                    d.lastTick, d.nextTick = 0, 0\n                end\n                \n                WeakAuras.ScanEvents(\"DOOM_UPDATE\")\n            end   \n    end)\nend\n\ne.Doom = PigmyShards_DoomTracking.DoomInfo\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
